import React, { useEffect, useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import { useForm } from 'react-hook-form';
import Button from '@material-ui/core/Button';
import { Link, useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { useToasts } from 'react-toast-notifications';
import API from '../../services/API';

const useStyles = makeStyles((theme) => ({
  btn: {
    backgroundColor: '#6d071a',
    textTransform: 'none',
    '&:hover': {
      backgroundColor: '#6d071a',
    },
  },
  input: {
    [theme.breakpoints.up('md')]: {
      width: '400px',
    },
    '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': {
      borderColor: '#6d071a',
    },
    '& .MuiOutlinedInput-input': {
      color: '#6d071a',
    },
    '& .MuiInputLabel-outlined.Mui-focused': {
      color: '#6d071a',
    },
  },
}));

const Register = () => {
  const classes = useStyles();
  const { register, handleSubmit, errors, watch } = useForm({
    mode: 'onSubmit',
  });
  const { addToast } = useToasts();

  useEffect(() => {
    if (errors) {
      const arrayErrors = Object.values(errors);
      arrayErrors.map((error) =>
        addToast(error.message, {
          appearance: 'error',
          autoDismiss: true,
        })
      );
    }
  }, [errors]);

  const password = useRef({});
  password.current = watch('password', '');

  const history = useHistory();

  const onSubmit = async (data) => {
    try {
      await API.post('users/', data);
      history.push('/login');
      addToast('Votre compte a été creé, vous pouvez vous connecter', {
        appearance: 'success',
        autoDismiss: true,
      });
    } catch (err) {
      if (err.response.status === 500) {
        addToast(
          'Erreur lors de votre inscription, veuillez rééssayer plus tard',
          {
            appearance: 'error',
            autoDismiss: true,
          }
        );
      }
    }
  };

  return (
    <div className='container-register-form'>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h1>Inscription</h1>
        <p className='line'>________________________</p>
        <div className='input-register-form'>
          <TextField
            className={classes.input}
            id='outlined-basic'
            label={'email'}
            variant='outlined'
            inputRef={register({
              required: 'Veuiller renseigner votre prénom.',
            })}
            name='email'
          />
        </div>
        <div className='input-register-form'>
          <TextField
            className={classes.input}
            type='password'
            id='outlined-basic'
            label={'Inscription.label5'}
            variant='outlined'
            inputRef={register({
              required: 'Veuillez renseigner un mot de passe',
            })}
            name='password'
          />
        </div>

        <div className='input-register-form'>
          <TextField
            className={classes.input}
            type='password'
            id='outlined-basic'
            label={'cofirm'}
            variant='outlined'
            inputRef={register({
              required: 'Veuillez confirmer votre mot de passe',
              validate: (value) =>
                value === password.current ||
                'Les mots de passe ne correspondent pas',
            })}
            name='encrypted_password'
          />
        </div>

        <div className='button-register-form'>
          <Button
            className={classes.btn}
            type='submit'
            variant='contained'
            color='primary'
          >
            {'Inscription.button'}
          </Button>
        </div>
        <div className='link-back-to-login'>
          <Link to='/login'>
            <p>{'Inscription.link'}</p>
          </Link>
        </div>
      </form>
    </div>
  );
};

export default Register;
