import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { CircularProgress } from '@material-ui/core';

export default function Register() {
  const [pokemons, setPokemons] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const { register, handleSubmit } = useForm();

  useEffect(() => {
    setIsLoading(false);
    axios
      .get('http://localhost:8080/api/pokemon')
      .then((response) => {
        setPokemons(response.data);
      })
      .catch(console.error);
  }, [isLoading]);

  const onSubmit = (data) => {
    console.log(data);
    axios
      .post('http://localhost:5000', data)
      .then((response) => setPokemons([...pokemons, response.data]));
    setIsLoading(true);
  };

  const onDelete = (pokemonId) => {
    axios
      .delete(`http://localhost:8080/api/pokemon/${pokemonId}`)
      .then((res) => {
        console.log(res.data);
        setIsLoading(true);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className='App'>
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input name='email' ref={register} placeholder='Name' />

          <input name='password' ref={register} placeholder='Type' />
          <input
            name='password_confirmation'
            ref={register}
            placeholder='Type'
          />

          <input type='submit' />
        </form>
      </div>

      {isLoading ? (
        <CircularProgress />
      ) : (
        <div>
          {pokemons.map((pokemon) => (
            <div key={pokemon.id}>
              <p>{pokemon.name}</p>
              <button type='button' onClick={() => onDelete(pokemon.id)}>
                x
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
