import React, { useEffect, useState } from 'react';

const ProductsDetail = (props) => {
  const id = props.match.params.id;
  const [article, setArticle] = useState([]);

  useEffect(() => {
    axios
      .get(`${URL}/articles/${id}`)
      .then((response) => {
        setArticle(response.data);
      })
      .catch(console.error);
  }, []);

  return (
    <>
      <h1>Bonjour </h1>
    </>
  );
};

export default ProductsDetail;
